olumns";a:2:{i:0;s:4:"post";i:1;s:4:"page";}s:12:"aiosp_do_log";s:0:"";s:22:"aiosp_google_publisher";s:0:"";s:15:"aiosp_ga_domain";s:0:"";s:21:"aiosp_ga_multi_domain";s:0:"";s:21:"aiosp_front_meta_tags";s:0:"";s:14:"aiosp_ex_pages";s:0:"";s:20:"aiosp_togglekeywords";s:1:"0";s:20:"aiosp_force_rewrites";s:1:"1";s:24:"aiosp_use_original_title";s:1:"0";s:16:"aiosp_cap_titles";s:2:"on";s:19:"aiosp_cpostadvanced";s:1:"0";s:17:"aiosp_cpostactive";a:2:{i:0;s:4:"post";i:1;s:4:"page";}s:17:"aiosp_cposttitles";s:0:"";s:15:"aiosp_admin_bar";s:2:"on";s:19:"aiosp_google_verify";s:0:"";s:17:"aiosp_bing_verify";s:0:"";s:22:"aiosp_pinterest_verify";s:0:"";s:20:"aiosp_unprotect_meta";s:0:"";s:37:"aiosp_wpcf7_contact_form_title_format";s:27:"%post_title% | %blog_title%";s:23:"aiosp_date_title_format";s:21:"%date% | %blog_title%";}', 'yes'),
